/*
|--------------------------------------------------------------------------
| Application Routes
|--------------------------------------------------------------------------
*/

Route::get('/', function(){return View::make('home.index');});
Route::post('user/new', array('uses' => 'user@new'));
Route::any('user/confirm', array('uses' => 'user@confirm'));
Route::post('user/auth', array('uses' => 'user@auth'));
Route::post('user/forgot_password', array('uses' => 'user@forgot_password'));

Route::group(array('before' => 'auth'), function() {
    Route::post('user/edit', array('uses' =>'user@edit'));
    Route::post('user/edit_password', array('uses' =>'user@edit_password'));
    Route::post('twitter/auth_url', array('uses' =>'twitteraccount@auth_url'));
    Route::post('twitter/new', array('uses'=>'twitteraccount@new_twitter_account'));
    Route::post('twitter/report', array('uses' => 'report@report'));
    Route::post('twitter/deactivate', array('uses' => 'twitteraccount@deactivate'));
    Route::post('twitter/activate', array('uses' => 'twitteraccount@activate'));

});

/*
|--------------------------------------------------------------------------
| Application 404 & 500 Error Handlers
|--------------------------------------------------------------------------
*/

Event::listen('404', function() {
	
	return Response::error('404');

});

Event::listen('500', function() {
	
	return Response::error('500');

});

Event::listen('laravel.query', function($sql) {

	#echo '<pre>'.var_dump($sql).'</pre>';

});

/*
|--------------------------------------------------------------------------
| Route Filters
|--------------------------------------------------------------------------
*/

Route::filter('before', function() { #verify if the client application is valid

	if (!URI::is('twitter/auth')) {

		$auth_app = new Services\Client\AuthApp(Input::get('avalue'), Input::get('atoken'));
		$auth = $auth_app->auth();

		if (array_key_exists("error", $auth)) {

			return Response::json($auth);

		}

	}

});

Route::filter('after', function($response) {
	// Do stuff after every request to your application...
});

Route::filter('csrf', function() {
	if (Request::forged()) return Response::error('500');
});

Route::filter('auth', function() { #valida o id e o token do usuÃ¡rio


	$auth = Auth::attempt(array(
		'uid'     => Input::get('uid'),
		'utoken'  => Input::get('utoken'))
	);
	
	if (array_key_exists('status', $auth)) {

		return Response::json($auth);

	}

});


